/**
 * Typefaces.
 */

@import './freight-sans';

/**
 * Stacks.
 */

:root {
  --text-sans: 'Freight Sans', 'Verdana', 'Geneva', sans-serif;
  --text-caps: 'Freight Sans Small Caps', 'Verdana', 'Geneva', sans-serif;
  --text-mono: 'Inconsolata', monospace;
}

/**
 * Sizes.
 */

:root {
  --text-small: 18px;
  --text-medium: 20px;
  --text-large: 24px;
  --text-huge: 30px;
}

/**
 * Headings.
 */

:root {
  --heading-small: 18px;
  --heading-medium: 20px;
  --heading-large: 30px;
  --heading-huge: 42px;
}

/**
 * Leadings.
 */

:root {
  --leading-small: 22px;
  --leading-medium: 30px;
  --leading-large: 38px;
  --leading-huge: 46px;
}

/**
 * Weights.
 */

:root {
  --weight-light: 300;
  --weight-book: 400;
  --weight-medium: 500;
  --weight-bold: 600;
}

