client/ui/components/column/index.css.src:
/**
 * Container.
 */

.Container {
  width: 100%;
  max-width: calc(var(--size-column) * 4 + var(--size-gutter) * 3);
  margin: 0 auto;
  padding: 0 calc(var(--size-gutter) / 2);
}

/**
 * Column.
 */

.Column,
[class*="Column--"] {
  float: left;
  width: 100%;
  margin-right: 60px;
}

.Column:last-child,
[class*="Column--"]:last-child {
  margin-right: none;
}

/**
 * By 4.
 */

.Column--1 {
  max-width: calc(var(--size-column) * 1 + var(--size-gutter) * 0);
}

.Column--2 {
  max-width: calc(var(--size-column) * 2 + var(--size-gutter) * 1);
}

.Column--3 {
  max-width: calc(var(--size-column) * 3 + var(--size-gutter) * 2);
}

.Column--4 {
  max-width: calc(var(--size-column) * 4 + var(--size-gutter) * 3);
}

/**
 * By 3.
 */

.Column--13 {
  max-width: calc(var(--size-column-third) * 1 + var(--size-gutter) * 0);
}

.Column--23 {
  max-width: calc(var(--size-column-third) * 2 + var(--size-gutter) * 1);
}

.Column--33 {
  max-width: calc(var(--size-column-third) * 3 + var(--size-gutter) * 2);
}

