client/ui/lib/prose/index.css.src:
/**
 * Prose.
 *
 * This should only be used for longer sections of running text, especially
 * when you want to present Markdown-formatted content, where people have no
 * control over classes.
 *
 * We purposely duplicate styles between other resuable classes and the `.Prose`
 * class, because it has slightly different cases, and re-using isn't worth it.
 */

.Prose {
  color: var(--text-primary);
}

/**
 * Sizes.
 *
 * The basic idea here is that whatever size we choose, all of the child
 * elements should render nicely.
 */

.Prose--small {
  font-size: var(--text-small);
  line-height: var(--leading-small);
  color: var(--text-secondary);
}

.Prose--medium {
  font-size: var(--text-medium);
  line-height: var(--leading-medium);
}

.Prose--large {
  font-size: var(--text-large);
  line-height: var(--leading-large);
}

.Prose--huge {
  font-size: var(--text-huge);
  line-height: var(--leading-huge);
}

/**
 * Spacing.
 */

.Prose--small > * + * {
  margin-top: calc(var(--leading-small) * .666);
}

.Prose--medium > * + * {
  margin-top: calc(var(--leading-medium) * .666);
}

.Prose--large > * + * {
  margin-top: calc(var(--leading-large) * .666);
}

.Prose--huge > * + * {
  margin-top: calc(var(--leading-huge) * .666);
}

/**
 * Links.
 */

.Prose a {
  color: var(--fern-1);
}

.Prose a:hover {
  color: var(--fern-2);
}

.Prose a:active {
  color: var(--fern-3);
}

/**
 * Headings.
 *
 * We've only styled three heading levels here, because prose shouldn't use
 * more than that, it's pretty impossible for user to keep track.
 */

.Prose h1,
.Prose h2,
.Prose h3,
.Prose h4,
.Prose h5,
.Prose h6 {
  color: var(--text-emphasized);
  font-weight: var(--weight-book);
}

.Prose h1 {
  font-size: var(--heading-huge);
  line-height: var(--leading-huge);
  margin-top: var(--size-huge);
}

.Prose h2 {
  font-size: var(--heading-large);
  line-height: var(--leading-large);
  margin-top: var(--size-large);
}

.Prose h3,
.Prose h4,
.Prose h5,
.Prose h6 {
  margin-top: var(--leading-medium);
  font-family: var(--text-caps);
  text-transform: lowercase;
  margin-bottom: calc(-1 * var(--leading-small));
}

.Prose h1 a,
.Prose h2 a,
.Prose h3 a,
.Prose h4 a,
.Prose h5 a,
.Prose h6 a {
  color: inherit;
}

/**
 * Formatting.
 */

.Prose strong,
.Prose b {
  color: var(--text-emphasized);
  font-weight: var(--weight-book);
}

.Prose em,
.Prose i {
  font-style: italic;
}

.Prose del,
.Prose s {
  text-decoration: strikethrough;
  color: var(--text-secondary);
}

.Prose ins {
  border-bottom: 1px dotted var(--text-primary);
}

.Prose mark {
  padding: 0 .2em;
  background-color: color(var(--banana-1) alpha(0.2));
}

/**
 * Small text.
 */

.Prose small {
  display: block;
  color: var(--text-secondary);
}

.Prose small strong,
.Prose small b {
  color: inherit;
}

.Prose--medium small {
  margin-top: calc(var(--leading-small) / 3);
  font-size: var(--text-small);
  line-height: var(--leading-small);
}

.Prose--large small {
  margin-top: calc(var(--leading-medium) / 3);
  font-size: var(--text-medium);
  line-height: var(--leading-medium);
}

/**
 * Images.
 */

.Prose img {
  max-width: 100%;
  margin-right: auto;
  margin-left: auto;
}

.Prose img + img {
  margin-top: var(--size-smaller);
}

/**
 * Blockquotes.
 */

.Prose blockquote {
  position: relative;
  padding-left: var(--size-medium);
  color: var(--text-secondary);
}

.Prose blockquote::after {
  content: '';
  position: absolute;
  left: 0;
  top: .33em;
  bottom: .33em;
  border-left: 4px solid color(var(--text-secondary) alpha(0.8));
}

.Prose blockquote small {
  display: block;
}

.Prose blockquote small::before {
  content: '— ';
}

/**
 * Lists.
 */

.Prose ul,
.Prose ol {
  padding-left: var(--size-medium);
  list-style-position: outside;
}

.Prose ul {
  list-style-type: disc;
}

.Prose ul ul {
  list-style-type: circle;
}

.Prose ul ul ul {
  list-style-type: square;
}

.Prose ol {
  list-style-type: decimal;
}

.Prose ol ol {
  list-style-type: lower-alpha;
}

.Prose ol ol ol {
  list-style-type: lower-roman;
}

.Prose--medium ul,
.Prose--medium ol {
  font-size: var(--text-small);
  line-height: var(--leading-small);
}

.Prose--large ul,
.Prose--large ol {
  font-size: var(--text-medium);
  line-height: var(--leading-medium);
}

.Prose--huge ul,
.Prose--huge ol {
  font-size: var(--text-large);
  line-height: var(--leading-large);
}

.Prose li {
  margin-bottom: .5em;
}

/**
 * Code.
 */

.Prose code {
  font-family: var(--text-mono);
}

.Prose :not(pre) > code,
.Prose pre {
  background-color: var(--ground-secondary);
  border: 1px solid var(--birch-35);
  border-radius: var(--size-radius);
}

.Prose :not(pre) > code {
  display: inline-block;
  line-height: 1.4;
  font-size: .6em;
  position: relative;
  top: -1px;
  padding: 0 .333em;
}

.Prose pre {
  font-size: 14px;
  line-height: 18px;
  padding: var(--size-small);
  overflow-x: auto;
  white-space: pre;
}

/**
 * Horizontal rules.
 */

.Prose hr {
  width: var(--size-column);
  margin: 3em auto;
  border: none;
  border-bottom: 2px solid var(--text-tertiary);
}

